create table TEMP_CLN_CONTENT_ORDER( CONTENT_ORDER BIGINT NOT NULL, ANCESTOR_ID BIGINT NOT NULL, DESCENDANT_ID BIGINT NOT NULL, DUMMY BIGINT NOT NULL );
insert into TEMP_CLN_CONTENT_ORDER (CONTENT_ORDER, ANCESTOR_ID, DESCENDANT_ID, DUMMY) select if(@prevtcs=ANCESTOR_ID, @rownum:=@rownum+1, @rownum:=0) as RANK, clnr.ANCESTOR_ID, clnr.DESCENDANT_ID, (@prevtcs:=ANCESTOR_ID) from CLN_RELATIONSHIP clnr join CAMPAIGN_LIBRARY_NODE cln on clnr.DESCENDANT_ID = cln.CLN_ID, (SELECT @rownum:=0) r, (SELECT @prevtcs:=0) s order by clnr.ANCESTOR_ID, cln.NAME;
insert into TEMP_CLN_CONTENT_ORDER (CONTENT_ORDER, ANCESTOR_ID, DESCENDANT_ID, DUMMY) select if(@prevtcs=LIBRARY_ID, @rownum:=@rownum+1, @rownum:=0) as RANK, clnr.LIBRARY_ID, clnr.CONTENT_ID, (@prevtcs:=LIBRARY_ID) from CAMPAIGN_LIBRARY_CONTENT clnr join CAMPAIGN_LIBRARY_NODE cln on clnr.CONTENT_ID = cln.CLN_ID, (SELECT @rownum:=0) r, (SELECT @prevtcs:=0) s order by clnr.LIBRARY_ID, cln.NAME;
update CLN_RELATIONSHIP set CONTENT_ORDER = ( select TEMP_CLN_CONTENT_ORDER.CONTENT_ORDER from TEMP_CLN_CONTENT_ORDER where TEMP_CLN_CONTENT_ORDER.ANCESTOR_ID = CLN_RELATIONSHIP.ANCESTOR_ID and TEMP_CLN_CONTENT_ORDER.DESCENDANT_ID = CLN_RELATIONSHIP.DESCENDANT_ID );
update CAMPAIGN_LIBRARY_CONTENT set CONTENT_ORDER = ( select TEMP_CLN_CONTENT_ORDER.CONTENT_ORDER from TEMP_CLN_CONTENT_ORDER where TEMP_CLN_CONTENT_ORDER.ANCESTOR_ID = CAMPAIGN_LIBRARY_CONTENT.LIBRARY_ID and TEMP_CLN_CONTENT_ORDER.DESCENDANT_ID = CAMPAIGN_LIBRARY_CONTENT.CONTENT_ID );
drop table TEMP_CLN_CONTENT_ORDER;

